import React, { Component } from 'react';
import { login } from '../../../store/actions/AuthActions';
import { connect } from 'react-redux';

class Login extends Component {

    state = {
        username: '',
        password: '',
        error: ''
    }

    changeHandler = event => {
        this.setState({
            [event.terget.name]: event.terget.value
        })
    }

    static getDerivedStateFromProps(nextProps, prevState) {
        if (
            JSON.stringify(nextProps.auth.error) !== JSON.stringify(prevState.error)
        ) {
            return {
                error: nextProps.auth.error
            };
        }
        return null;
    }

    onSubmitHanddler = event => {
        event.preventDefault()
        this.props.login({
            email: this.state.email,
            password: this.state.password
        }, this.props.history)
    }

    render() {
        let { username, password, error } = this.state;
        return(
            <body className="hold-transition login-page">
                <div className="login-box">
                    <div className="login-logo">
                        <a href="/"><b>Amader news</b>24</a>
                    </div>
                    <div className="card">
                        <div className="card-body login-card-body">
                        <p className="login-box-msg">Sign In</p>

                        <form onSubmit={this.onSubmitHanddler}>
                            <div className="input-group mb-3">
                                <input
                                    type="email"
                                    name="email"
                                    value={username}
                                    onChange={this.changeHandler}
                                    className="form-control"
                                    placeholder="Email"
                                />
                            <div className="input-group-append">
                                <div className="input-group-text">
                                <span className="fas fa-envelope"></span>
                                </div>
                            </div>
                            </div>
                            <div className="input-group mb-3">
                                <input
                                    type="password"
                                    name="password"
                                    value={password}
                                    onChange={this.changeHandler}
                                    className="form-control"
                                    placeholder="******"
                                />
                            <div className="input-group-append">
                                <div className="input-group-text">
                                <span className="fas fa-lock"></span>
                                </div>
                            </div>
                            </div>
                            <div className="row">
                                <div className="col-8">
                                    <div className="icheck-primary">
                                    <input type="checkbox" id="remember"/>
                                    <label for="remember">
                                        Remember Me
                                    </label>
                                    </div>
                                </div>
                                <div className="col-4">
                                    <button type="submit" class="btn btn-primary btn-block btn-sm">Sign In</button>
                                </div>
                            </div>
                        </form>
                        <p className="mb-1">
                            <br />
                            <a className="mr-5" href="/forgot-password">Forgot my password</a> <a href="/registration" className="text-center">Register a user</a>
                        </p>
                        </div>
                    </div>
                </div>
            </body>
        )
    }
}

const mapStateToProps = state => ({
    auth: state.auth
})

export default connect(mapStateToProps, {login}) (Login)
